    private void bguardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bguardarActionPerformed
        if(txtid.getText().equals("")){
        String nombre = this.txtnombre.getText();
        String fecha = this.txtfecha.getText();
        String horainicio = this.txthorainicio.getText();
        String horafin = this.txthorafin.getText();
        String lugar = this.txtlugar.getText();
        String nota = this.jtnota.getText();
        if (nombre.equals("") || fecha.equals("") || horainicio.equals("") || horafin.equals("") || lugar.equals("") || nota.equals("")) {
            JOptionPane.showMessageDialog(null, "Favor llenar todos los campos...");

        } else {
          
            try {
                  Entidades entidad = new  Entidades();
                  entidad.setNombre(nombre);
                  entidad.setFecha(fecha);
                  entidad.setHoraInicio(horainicio);
                  entidad.setHoraFin(horafin);
                  entidad.setLugar(lugar);
                  entidad.setNota(nota);
                   Funciones.guardar(entidad);
                   Funciones.Listar("");
                JOptionPane.showMessageDialog(null, "Los datos han sido insertados con éxito", "Operación Exitosa",
                            JOptionPane.INFORMATION_MESSAGE);
            
                
            } catch (Exception ex) {
                ex.printStackTrace();

            }
        }

        nuevo();}
        
        else{
            
        String nombre = this.txtnombre.getText();
        String fecha = this.txtfecha.getText();
        String horainicio = this.txthorainicio.getText();
        String horafin = this.txthorafin.getText();
        String lugar = this.txtlugar.getText();
        String nota = this.jtnota.getText();
        int id = Integer.parseInt(this.txtid.getText());
        if (nombre.equals("") || fecha.equals("") || horainicio.equals("") || horafin.equals("") || lugar.equals("") || nota.equals("")) {
            JOptionPane.showMessageDialog(null, "Favor llenar todos los campos...");

        } else {
            try {
                  Entidades entidad = new  Entidades();
                  entidad.setNombre(nombre);
                  entidad.setFecha(fecha);
                  entidad.setHoraInicio(horainicio);
                  entidad.setHoraFin(horafin);
                  entidad.setLugar(lugar);
                  entidad.setNota(nota);
                  entidad.setId(id);
                   Funciones.modificar(entidad);
                   Funciones.Listar("");
                JOptionPane.showMessageDialog(null, "Los datos han sido modificado con éxito", "Operación Exitosa",
                            JOptionPane.INFORMATION_MESSAGE);
                
                
            } catch (Exception ex) {
                ex.printStackTrace();

            }
        }

        nuevo();
        
            
        }
    }//GEN-LAST:event_bguardarActionPerformed

