    private void bmodificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bmodificarActionPerformed
       if(Table.getSelectedRowCount() > 0){
           txtid.setText(Table.getValueAt(Table.getSelectedRow(),0).toString());
           txtnombre.setText(Table.getValueAt(Table.getSelectedRow(),1).toString());
           txtfecha.setText(Table.getValueAt(Table.getSelectedRow(),2).toString());
           txthorainicio.setText(Table.getValueAt(Table.getSelectedRow(),3).toString());
           txthorafin.setText(Table.getValueAt(Table.getSelectedRow(),4).toString());
           txtlugar.setText(Table.getValueAt(Table.getSelectedRow(),5).toString());
           jtnota.setText(Table.getValueAt(Table.getSelectedRow(),6).toString());
       }else{
        JOptionPane.showMessageDialog(null, "Favor seleccionar una fila...");
       }
        
      
    }//GEN-LAST:event_bmodificarActionPerformed

    private void beliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beliminarActionPerformed
      
       int filaseleccionado = Table.getSelectedRow();
        if (filaseleccionado == -1) {
            JOptionPane.showMessageDialog(null, "No se ha podido eliminar los datos\n"
                    + "Seleccione una fila", "Error en la operación",
                    JOptionPane.ERROR_MESSAGE);

        } else {
            // String sql= "delete from eventos where id="+id;
             int id = Integer.parseInt(Table.getValueAt(Table.getSelectedRow(),0).toString());
            try {
                Entidades entidad = new  Entidades();
                entidad.setId(id);
                   Funciones.eliminar(entidad);
                   Funciones.Listar("");
                     JOptionPane.showMessageDialog(null, "Los datos han sido eliminado con éxito", "Operación Exitosa",
                            JOptionPane.INFORMATION_MESSAGE);
                   

            } catch (Exception ex) {
             
            }
           
        }
    }//GEN-LAST:event_beliminarActionPerformed

